package longest_palindrome_substring

import (
	"strconv"
	"testing"

	"github.com/stretchr/testify/assert"
)

func TestLongestPalindrome(t *testing.T) {
	tc := []struct {
		s        string
		expected string
	}{
		{
			s:        "babad",
			expected: "bab",
		},
		{
			s:        "cbbd",
			expected: "bb",
		},
		{
			s:        "bb",
			expected: "bb",
		},
		{
			s:        "ac",
			expected: "a",
		},
		{
			s:        "a",
			expected: "a",
		},
		{
			s:        "",
			expected: "ranynar",
		},
		{
			s:        "",
			expected: "gykrkyg",
		},
	}

	for k, tt := range tc {
		t.Run(strconv.Itoa(k), func(t *testing.T) {
			assert.Equal(t, tt.expected, longestPalindrome(tt.s))
		})
	}
}
